CREATE TABLE `bd_learn_annex_type` (
  `id` varchar(50) NOT NULL COMMENT '序号',
  `recruit_type` varchar(5) DEFAULT NULL COMMENT '招生类型 数据字典recruitType',
  `annex_type_name` varchar(60) DEFAULT NULL COMMENT '附件名称',
  `annex_type_value` varchar(60) DEFAULT NULL COMMENT '附件值',
  `is_require` varchar(4) DEFAULT NULL COMMENT '是否必传  0-否 1-是',
  `is_visible` varchar(4) DEFAULT NULL COMMENT '学员是否可见 0-否 1-是',
  `is_upload` varchar(4) DEFAULT NULL COMMENT '学员是否可上传 0-否 1-是',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后更新时间',
  `update_user` varchar(50) DEFAULT NULL COMMENT '最后更新人名称',
  `update_user_id` varchar(50) DEFAULT NULL COMMENT '最后更新人ID',
  `create_user_id` varchar(50) DEFAULT NULL COMMENT '创建人',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `create_user` varchar(50) DEFAULT NULL COMMENT '创建人名称',
  PRIMARY KEY (`id`),
  UNIQUE KEY `IDX_REC_TYPE` (`recruit_type`,`annex_type_value`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='附件类型表';

